                                    </svg>
                                </div>
                            </div>
                            <div class="dropdown-menu dropdown-menu_item_list_sidebar_catalog_menu" v-if="types.types">
                                <a class="dropdown-item"  v-for="type in types.types" v-if="cat.id == type.category_id" @click="redirectToCatalog(cat,type)">{{type.name}}</a>
                            </div>
                        </div>
                    </vue-scroll>
                </div>

        </div>
    </header>
</template>
<script>

import vuescroll from 'vuescroll'
import {mapActions, mapState} from 'vuex'
export default {
    name:"navbar-catalog",
    components:{
        vuescroll
    },
    data(){
        return {
            sidebarExpand:false,
            types:[],
            categories:[],
            scrollPosition:null,
            ops: {
                scrollPanel: {
                    initialScrollY: false,
                    initialScrollX: false,
                    scrollingX: true,
                    scrollingY: true,
                    speed: 400,
                    easing: undefined,
                    verticalNativeBarPos: 'right'
                }

            }
        }
    },
    methods: {
        getData() {
            axios.get('/get/types/')
                .then((response) => {

                    if (response.data !== 'undefined' && response.data !== null) {
                        this.types = response.data;
                    }
                })

            axios.get('/get/categories/')
                .then((response) => {

                    if (response.data !== 'undefined' && response.data !== null) {
                        this.categories = response.data;
                    }
                })
        },
        updateScroll() {
            this.scrollPosition = window.scrollY
        },

        redirectToCatalog(cat, type)
        {
            this.$store.dispatch('catalog/redirectToCatalog', {
                cat:cat,
                type:type
            }).then(
                ()=>{
                    if(this.$route.name !== 'catalog')
                    {
                        this.$router.push({'name':'catalog'})
                    }
                }
            )


        }
    },
    mounted() {
        this.getData();
        this.$store.dispatch('catalog/getUserIP');
        window.addEventListener('scroll', this.updateScroll);
    },
    computed: {
        ...mapState('catalog',['location'])
    },
}
</script>
